def solution(reference, track):
    answer = 1000
    reLen = len(reference)
    trLen = len(track)
    while trLen:
        si, i, j, cnt, maxCnt = 0, 0, 0, 0, 0
        while si < reLen and i < reLen and j < trLen:
            if reference[i] == track[j]:
                i += 1
                j += 1
                cnt += 1
            else:
                si += 1
                i, j = si, 0
                maxCnt = max(maxCnt, cnt)
            if i == reLen or j == trLen:
                maxCnt = max(maxCnt, cnt)
        track = track[maxCnt:]
        answer = min(answer, maxCnt)
        trLen = len(track)
    return answer



print(solution('dfwnelaienmtglaidpqkernasdkfnasdfnklasopaukyjadgflukqkfhuskadufasikdoeijfoqwiefoqweiohqwefddfoqwerih', ''))

